# Stitch to (x, y).
NORMAL                       = 0x00
# Move to (x, y).
JUMP                         = 0x01
# Trim and move to (x, y).
TRIM                         = 0x02
# Pause machine for a thread change.
STOP                         = 0x04
# Add a sequin at the current co-ordinates.
SEQUIN                       = 0x08
# End of program.
END                          = 0x10


